  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"introduction"}}>{`Introduction`}</MDXTag>
<MDXTag name="p" components={components}>{`Polvokit is a React-based UI library.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"installation"}}>{`Installation`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`yarn add polvokit
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`You also have to install its peer dependencies:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`yarn add styled-components styled-tools polished styled-media-query react-router-dom react-select date-fns msk
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`Just import the components you want to use from the library:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`import { Button, Panel } from 'polvokit'

export default function MyComponent () {
  return (
    <Panel title='Heading'>
      <Button>Click me</Button>
    </Panel>
  )
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
